parameters.query:
INSERT INTO processing_errors (
  error_code, error_message, error_details, input_data,
  workflow_step, retry_count, resolved, execution_id
) VALUES (
  {{ $json.error?.code || null }},
  {{ $json.error?.message || 'Unknown error' }},
  {{ JSON.stringify($json.error || {}) }},
  {{ JSON.stringify($json.execution?.data?.resultData || {}) }},
  {{ $json.error?.node?.name || null }},
  0,
  FALSE,
  {{ $json.execution?.id || null }}
);
